import React, { useState } from 'react';
import { Bar } from 'react-chartjs-2';
import '.calculate.css';

const questions = [
  {
    question: "你家有多少人?",
    options: ["一个", "两个", "三个", "四个", "五个及以上"],
    type: "single", // 单选
  },
  {
    question: "选择你家中使用的电器。（可多选）",
    options: ["电炉/烤箱", "微波炉", "洗衣机", "烘干机", "洗碗机", "冰箱", "电视", "空调", "加热器"],
    type: "multiple", // 多选
  },
  // 可以根据需要继续添加其他问题...
];

function Calculate() {
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [showChart, setShowChart] = useState(false);
  const [fade, setFade] = useState('');

  const handleNext = () => {
    setFade('fade-exit');
    setTimeout(() => {
      if (currentQuestionIndex < questions.length - 1) {
        setCurrentQuestionIndex(currentQuestionIndex + 1);
      } else {
        setShowChart(true);
      }
      setFade('fade-enter-active');
    }, 500); // 确保这个时间与CSS中的过渡时间一致
  };

  const chartData = {
    labels: ["一个", "两个", "三个", "四个", "五个及以上"],
    datasets: [
      {
        label: '家庭人数',
        data: [2, 3, 5, 1, 4], // 示例数据
        backgroundColor: 'rgba(54, 162, 235, 0.6)',
      },
    ],
  };

  const chartOptions = {
    scales: {
      y: {
        beginAtZero: true,
      },
    },
  };

  return (
    <div>
      {!showChart ? (
        <div className={`fade ${fade}`}>
          <h2>{questions[currentQuestionIndex].question}</h2>
          {questions[currentQuestionIndex].options.map((option, index) => (
            <div key={index}>
              <input type={questions[currentQuestionIndex].type === "single" ? "radio" : "checkbox"} id={`option-${index}`} name="option" value={option} />
              <label htmlFor={`option-${index}`}>{option}</label>
            </div>
          ))}
          <button onClick={handleNext}>下一题</button>
        </div>
      ) : (
        <div className={`fade ${fade}`}>
          <Bar data={chartData} options={chartOptions} />
        </div>
      )}
    </div>
  );
}

export default Calculate;
